{
  "comment" : [
    "Default event tracker for HBase services.",
    "Defines equality by comparing username, action, table name, family ",
    "and qualifier of the events."
  ],
  "timeToLive" : 60000,
  "fields" : [
    { "type": "value", "name" : "tableName" },
    { "type": "value", "name" : "family" },
    { "type": "value", "name" : "qualifier" },
    { "type": "value", "name" : "operation" },
    { "type": "username", "name" : "username" }
  ]
}

{
  "comment" : [
    "Default filter for HBase services.",
    "Discards events that affect the internal -ROOT-, .META. and _acl_ tables."
  ],
  "defaultAction" : "accept",
  "rules" : [
    {
      "action" : "discard",
      "fields" : [
        { "name" : "tableName", "match" : "(?:-ROOT-|.META.|_acl_|hbase:meta|hbase:acl)" }
      ]
    }
  ]
}
