value:
{
  "comment" : [
    "The default Hive audit event filter discards HDFS directory events ",
    "generated by Hive jobs that reference the /tmp directory."
  ],
  "defaultAction" : "accept",
  "rules" : [
    {
      "action" : "discard",
      "fields" : [
        { "name" : "operation", "match" : "QUERY" },
        { "name" : "objectType", "match" : "DFS_DIR"},
        { "name" : "resourcePath", "match" : "/tmp/hive-(?:.+)?/hive_(?:.+)?/-mr-.*" }
      ]
    }
  ]
}
